<template>
  <div class="fs-image-format">
    <component :is="$fsui.imageGroup.name" v-bind="wrapper">
      <component
        :is="$fsui.image.name"
        v-for="url in computedUrls"
        :key="url"
        class="fs-image-item"
        :src="url"
        v-bind="binding"
      >
        <template #placeholder>
          <div class="fs-image-slot">
            <fs-loading :loading="true" />
          </div>
        </template>

        <template #error>
          <div class="fs-image-slot">
            <img :src="error" style="max-width: 50%" />
          </div>
        </template>
      </component>
    </component>
  </div>
</template>

<script>
  // 图片行展示组件
  export default {
    name: 'FsImagesFormat',
    inheritAttrs: false,
    props: {
      //包裹image的组件配置，antdv是preview-group，element是div
      wrapper: {},
      // 图片的url
      // 'url' 或 ['url1','url2']
      modelValue: {
        type: [String, Array],
        require: true,
      },
      error: {
        default:
          'data:image/png;base64,',
      },
      // 构建下载url方法
      buildUrl: {
        type: Function,
        default: function (value) {
          return value
        },
      },
    },
    data() {
      return {}
    },
    computed: {
      computedUrls() {
        const urls = []
        if (this.modelValue == null || this.modelValue === '') {
          return urls
        }
        if (typeof this.modelValue === 'string') {
          urls.push(this.modelValue)
        } else if (this.modelValue instanceof Array) {
          for (const item of this.modelValue) {
            if (item == null) {
              continue
            }
            if (item.url != null) {
              urls.push(item.url)
            } else {
              urls.push(item)
            }
          }
        } else if (this.modelValue.url) {
          urls.push(this.modelValue.url)
        }
        if (!this.buildUrl) {
          return urls
        }
        const arr = []
        for (const url of urls) {
          arr.push(this.buildUrl(url))
        }
        return arr
      },

      binding() {
        const preview = this.$fsui.image.buildPreviewList(this.computedUrls)
        return {
          fit: 'contain',
          ...this.$attrs,
          ...preview,
        }
      },
    },
    methods: {
      handleClick() {
        // this.$emit('input', !this.modelValue)
      },
    },
  }
</script>
